(2, 'Extended-stay hotels', 'Hotels designed for longer stays and typically include amenities like kitchenettes, laundry facilities, and larger living spaces.', '$80 - $200 per night', NULL, NULL),
(3, 'Business hotels', 'Hotels located in business districts that offer services and amenities tailored to business travelers, such as meeting rooms, high-speed internet, and business centers.', '$100 - $300 per night', NULL, NULL),
(4, 'Boutique hotels', 'Smaller, intimate hotels that focus on unique and stylish design, personalized service, and often located in trendy or unique locations.', '$100 - $400 per night', NULL, NULL),
(5, 'Eco-friendly hotels', 'Hotels that prioritize sustainability and often have features such as energy-efficient appliances, eco-friendly cleaning products, and recycling programs.', '$100 - $400 per night', NULL, NULL),
(6, 'Resort hotels', 'Hotels often located in scenic locations, offering a wide range of amenities and activities, including golf courses, water sports, and other recreational activities.', '$150 - $500+ per night', NULL, NULL),
(7, 'Luxury hotels', 'Upscale hotels that provide the highest level of service and amenities, often targeted at high-end travelers. They often feature amenities like spas, fine dining restaurants, and high-quality room furnishings.', '$300 - $1000+ per night', NULL, NULL);
